import re

def arc_to_matrix(matrix, arc):
	# print("ARC",arc)
	for item in arc:
		# print("ITEM", item)
		if(item[2] == 'CAN'):
			# print(item[1], item[0], item[2], "aa")
			matrix[item[1]][item[0]] = 0
		if(item[2] == 'WAL'):
			# print(item[1], item[0], item[2], "aa")
			matrix[item[1]][item[0]] = 2
	
	return matrix

def matrix_to_arc(matrix, attack = False):
	arc = []
	for y in range(0, 36):
		for x in range(0, 64):
			if(not attack):
				if(matrix[y][x] == 0):
					arc.append((x, y, 'CAN'))
				elif(matrix[y][x] == 2):
					arc.append((x, y, 'WAL'))
			if(attack):
				if(matrix[y][x] == 0):
					arc.append((x, y, 'CANA'))
				elif(matrix[y][x] == 2):
					arc.append((x, y, 'WAL'))
	return arc


def create_empty_matrix():
	whole = []
	for k in range(0, 36):
		row = []
		for j in range(0,64):
			row.append(1)
		whole.append(row)
	return whole

