def stringtolist(matrix):
	matrix = matrix.split(',')

	row = []
	whole = []

	for k in range(0, 36):
		row = []
		for j in range(0,64):
			row.append(int(re.search(r'\d+', matrix[(k * 64) + j]).group()))
		whole.append(row)
	# print(whole)

	# print("FIRST FIRST", whole[1][1]) #Should be 2

	return whole

